package nl.mmeiboom.adventofcode.y2022

import nl.mmeiboom.adventofcode.lib.Day

object Y2022D06 : Day {
    private const val input = ""

    override fun part1(): Any {
        return findFirstUniqueSequence(4)
    }

    override fun part2(): Any {
        return findFirstUniqueSequence(14)
    }

    private fun findFirstUniqueSequence(length: Int): Int {
        return input.indices
            .first { input.substring(it, it + length).toSet().size == length } + length
    }
}